  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="p" components={components}><MDXTag name="img" components={components} parentName="p" props={{"src":"/hsf/img/api_overview_diagram.svg","alt":"Image"}}></MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`REflex is a user-centric pub/sub system that uses `}<MDXTag name="a" components={components} parentName="p" props={{"href":"https://www.w3.org/TR/ldn/"}}>{`Linked Data Notifications (LDN)`}</MDXTag>{` to enable sharing and reuse of notifications across applications, regardless of how they were generated. `}</MDXTag>
<MDXTag name="p" components={components}>{`Instead of treating notifications as ephemeral or non-persistent entities, this specification enables the notion of a notification as an individual entity with its own URI. As such, notifications can be retrieved and reused. `}</MDXTag>
<MDXTag name="p" components={components}>{`The API is very simple, the only interactions are publish a message (HTTP POST) and get messages either pushed to your application or with HTTP GET.  All the magic is in the `}<MDXTag name="a" components={components} parentName="p" props={{"href":"schema-introduction.md"}}>{`schemas`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"more-information"}}>{`More Information`}</MDXTag>
<MDXTag name="p" components={components}>{`For more information, see `}<MDXTag name="a" components={components} parentName="p" props={{"href":"docs-developer-resources.md"}}>{`Developer Resources`}</MDXTag></MDXTag>
<MDXTag name="hr" components={components}></MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-sequence"}}>{`# diagram generated by [js-sequence](https://bramp.github.io/js-sequence-diagrams/)

Producer->User/inbox: publish {message}
User/inbox-->Consumer: Http:post {message}
User/inbox-->Consumer: SNS:publish {message}
User/inbox-->Consumer: SQS:send {message}
User/inbox-->Consumer: Lambda:invoke {message}
Note over User/inbox,Consumer: delivery options
`}</MDXTag></MDXTag>
           </MDXTag>
  }
}
